import React, { useState, useRef } from 'react';
import './App.css'

function App() {
  const [ firstNumber, setFirstNumber] = useState(randomNumber());
  const [ secondNumber, setSecondNumber] = useState(randeomNumber());
  const [ result, setResult ] = useState('');
  const [ value, setValue ] = useState('');
  const inputRef = useRef();

  function randomNumber(){
    return Math.ceil(Math.random()*9);
  }

  const onSubmit = (e) =>{
    e.preventDefault();
    if (parseInt(value) === firstNumber * secondNumber){
      setResult('정답입니다.');
      setValue('');
      newQuize();
      inputRef.current.focus();
    } else {
      setResult('틀렸어요!');
      setValue('');
      inputRef.current.focus();
    }
  };

  const userInput = (e) => {
    setValue(e.target.value);
  };

  const newQuiz = () => {
    setFirstNumber(randomNumber());
    setSecondNumber(randomNumber());
  };

  return (
    <div className="container">
      <form onSubmit={onSubmit}>
        <h2 className='title'>타이틀</h2>
        <p className='quiz'>
          문제 : {firstNumber} x {secondNumber}
        </p>
        <input ref={inputRef} value={value} onChange={userInput} placeholder='정답을 입력하세요.' type='number' />
        <button>확인</button>
        <p className='result'>{result}</p>
      </form>
    </div>
  );
}

export default App;
